mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_user = #{updateUser}, update_time = #{updateTime},
where id = #{id}

mapper.delete:
delete s , sd from setmeal s join setmeal_dish sd on s.id = sd.setmeal_id

    s.id in

        #{id}

mapper.select:
select s.* , c.name as categoryName from setmeal s left outer join category c on s.category_id = c.id


        and s.name like concat('%',#{name},'%')


        and s.category_id = #{categoryId}


        and s.status = #{status}


order by id desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
